import React , {useState} from 'react';
import Conditions from '../Conditions/Conditions';
import styles from './Forecast.module.css';


const Forecast = () => {

    const [unit,setUnit] = useState();
    const [enteredCity, setEnteredCity] = useState("Beograd");
    let [responseObj, setResponseObj] = useState({});
    //nacin1
    /*const getForecast =  async () => {
        try{
            const res = await axios.get(
                "https://api.openweathermap.org/data/2.5/weather?q=Zagreb&appid=5a4e05662564bdefbc9d1b3d65c255f0"
            )
            console.log(res.data);

        } catch(err){
            console.error(err);
        }
    };*/

    //nacin2
    /*const getForecast = async () => {
        console.log("halo");
        const res = await axios.get(
            "https://api.openweathermap.org/data/2.5/weather?q=Zagreb&appid=5a4e05662564bdefbc9d1b3d65c255f0"
        )
        console.log(res.data.main.temp);
        console.log(res.data.main.temp_max);
        console.log(res.data.main.temp_min);
    }*/

    //nacin3
    const getForecast = (e) => {
        e.preventDefault();
        console.log("daj prognozu!");
        if(enteredCity.trim().length === 0){
            console.log("ERROR MODAL ILI SAMO ZACRVENI");
        }

        fetch(`https://api.openweathermap.org/data/2.5/weather?q=${enteredCity}&units=${unit}&appid=5a4e05662564bdefbc9d1b3d65c255f0`
        ).then(response => response.json()).then(response => {
           setResponseObj(response)
       })
    }

    /*const enteredCityHandler = (event) => {
        console.log("ovo je input: "+event.target.value);
        setEnteredCity(encodeURIComponent(event.target.value));
    };*/

    return(
        <div>
           <h2>Find Current Weather Conditions</h2>
           <form onSubmit={getForecast}>
                <input
                    type="text"
                    placeholder="Enter City"
                    maxLength="50"
                    value={enteredCity}
                    onChange={(e) => setEnteredCity(e.target.value)}
                    />

                <label>
                    <input
                        type="radio"
                        name="units"
                        checked={unit === "imperial"}
                        value="imperial"
                        onChange={(e) => setUnit(e.target.value)}
                        />
                    Fahrenheit
                </label>
                <label>
                    <input
                        type="radio"
                        name="units"
                        checked={unit === "metric"}
                        value="metric"
                        onChange={(e) => setUnit(e.target.value)}
                        />
                    Celsius
                </label>
                <button type="submit">Get Forecast</button>
            </form>
           <Conditions responseObj={responseObj}/>
       </div>
    );

}

export default Forecast;
