import React , {useState , useRef} from 'react';
import Card from '../UI/Card';
import styles from "./Login.module.css";

const Login = (props) => {

    const emailRef = useRef();
    const passwordRef = useRef();

    const [error, setError] = useState({title : "",
                                        message : ""});

    const loginHandler = (event) => {
        event.preventDefault();
        console.log(" ovo je ref "+emailRef.current.value);
        console.log(" ovo je ref "+passwordRef.current.value);

        if(emailRef.current.value.trim().length === 0|| passwordRef.current.value.trim().length === 0){
            setError({
                title : "Invalid email or password",
                message : "Try again - non empty"
            })
            console.log("Error 1");
            console.log(error.message);
            return;
        }

        if(!emailRef.current.value.trim().includes('@')){
            setError({
                title : "Invalid email",
                message : "Try again - email must contain @"
            })
            console.log("Error 2");
            console.log(error.message);
            return;
        }

        const enteredUser = {
            email : emailRef.current.value,
            password : passwordRef.current.value
        };

        console.log("u Login.js "+enteredUser.email);

        props.onLogin(enteredUser);
        emailRef.current.value = '';
        passwordRef.current.value = '';

    }

    return (
        <div>
            <Card className={styles.input}>
                <form onSubmit={loginHandler}>
                    <label htmlFor="email">Email</label>
                    <input id="email" type="text" ref={emailRef} ></input>
                    <label htmlFor="password" >Password</label>
                    <input id="password" type="text" ref={passwordRef}></input>
                    <button type="submit" className={styles.button}>Add User</button>
                </form>
            </Card>
        </div>

    );
}

export default Login;
