import react , {useEffect , useState} from "react";
import { useParams } from "react-router";
import TableForecast from "./TableForecast";

const FiveDayForecast = (props) => {

    const [data,setData] = useState();
    const [dailyWeatherList, setDailyWeatherList] = useState([]);
    const params = useParams();

    const getMonth = (m) => {
        if (m==="01") return "Jan"
        else if (m==="02") return "Feb"
        else if (m==="03") return "Mar"
        else if (m==="04") return "Apr"
        else if (m==="05") return "May"
        else if (m==="06") return "Jun"
        else if (m==="07") return "Jul"
        else if (m==="08") return "Aug"
        else if (m==="09") return "Sep"
        else if (m==="10") return "Oct"
        else if (m==="11") return "Nov"
        else if (m==="12") return "Dec"
        return "njemaaa";
    }

    const fetchData = ( async () => {
        const response = await fetch(`https://api.openweathermap.org/data/2.5/forecast?q=${params.cityId}&units=metric&appid=5a4e05662564bdefbc9d1b3d65c255f0`);
        const res = await response.json();
        console.log(res); 
        setData(res);
    });

    useEffect(() => {
        fetchData();
    }, []);

    let content = <p>Nema nista</p>;

    if (data) {

        content = <img src={`http://openweathermap.org/img/w/${data.list[0].weather[0].icon}.png`}/>

        console.log("datum je ");
        let d = data.list[0].dt_txt.split(" ")[0].split("-");
        console.log(d);

        let month = getMonth(d[1]);
        console.log(month)
        var date = new Date(d[2]+month+d[0]);
        var day = date.getDay();
        var daysName = ['Sunday','Monday','Tuesday','Wednesday','Thursday','Friday','Saturday'];
        console.log(daysName[day]);


        console.log("max temp je");
        console.log(data.list[0].main.temp_max);
        console.log("min temp je ");
        console.log(data.list[0].main.temp_min);

        const dailyWeather = {
            dayName : daysName[day],
            icon : <img src={`http://openweathermap.org/img/w/${data.list[0].weather[0].icon}.png`}/>,
            minTemp : data.list[0].main.temp_min,
            maxTemp : data.list[0].main.temp_max,

        }

        setDailyWeatherList((prevDailyWeathers) => {
            const updated = [...prevDailyWeathers,dailyWeather];
            return updated;
        });

        //sada podatke stavi u objekt(podatke za 1 dan)
        //onda ih mozes staviti u listu
        //i iz te liste dolje ocitavat u neku drugu komponentu koja ce primati gore objekt i fino ih prikazivati

    }


    return(
        <div>
            <div>Ovo je petodnevna prognoza</div>
            <section>{content}</section>
            <TableForecast fiveDays={dailyWeatherList} />
        </div>
    );
}

export default FiveDayForecast;
