//import logo from './logo.svg';
import React , {useContext} from "react";
import { Route , Switch} from "react-router-dom";
import './App.css';
import Forecast from './components/pages/Forecast/Forecast';
import Login from "./components/Login/Login";
import AuthContext from "./store/auth-context";

function App() {

  const authCtx = useContext(AuthContext);

  const storeUser = (token) => {
    console.log("in App.js "+token);
  };

  return (
    <div className="App">
      <Switch>
        <Route path = "/weather" exact>
          <header className="App-header">
            <h1>React Weather App</h1>
          </header>
          <main className="main">
            {authCtx.isLoggedIn && <Forecast />}
            {!authCtx.isLoggedIn && <Login onLogin={storeUser}/>}
          </main>
          <footer className="footer">
            Page created by Tihana
          </footer>
        </Route>
        <Route path="/" exact>
          <Login onLogin={storeUser} />
        </Route>
      </Switch>
    </div>
  );
}

export default App;
