import react , {useEffect , useState} from "react";
import { useParams } from "react-router";
import WeekContainer from "../WeeklyForecast/WeekContainer";

const FiveDayForecast = () => {

    const [dailyWeatherList, setDailyWeatherList] = useState([]);
    const params = useParams();

    const getMonth = (m) => {
        if (m==="01") return "Jan"
        else if (m==="02") return "Feb"
        else if (m==="03") return "Mar"
        else if (m==="04") return "Apr"
        else if (m==="05") return "May"
        else if (m==="06") return "Jun"
        else if (m==="07") return "Jul"
        else if (m==="08") return "Aug"
        else if (m==="09") return "Sep"
        else if (m==="10") return "Oct"
        else if (m==="11") return "Nov"
        else if (m==="12") return "Dec"
        return "njemaaa";
    }

    //mogla sam ovdje koristiti one call api jer ima daily za 7 dana-PROMIJENI KASNIJE!!
/*  const fetchData = ( async () => {
        const response = await fetch(`https://api.openweathermap.org/data/2.5/forecast?q=${params.cityId}&units=metric&appid=5a4e05662564bdefbc9d1b3d65c255f0`);
        const data = await response.json();
        console.log(data);
*/

        /*const dailyData = data.list.filter(reading => {   
            return reading.dt_txt.includes("18:00:00")
            }
          )
        console.log("ovo je filtrirano");
        console.log(dailyData);*/


 /*       for (let i = 0; i <40; i=i+8) {

            let data_time = data.list[i].dt_txt.split(" ")[0].split("-");

            let month = getMonth(data_time[1]);
            var date = new Date(data_time[2]+month+data_time[0]);
            var day = date.getDay();
            var daysName = ['Sun','Mon','Tue','Wed','Thu','Fri','Sat'];


            const dailyWeather = {
                dayName : daysName[day],
                //icon : "data.list[i].weather[0].id",
                icon : <img src={`http://openweathermap.org/img/w/${data.list[i].weather[0].icon}.png`}/>,
                minTemp : data.list[i].main.temp_min,
                maxTemp : data.list[i].main.temp_max,

            }

            setDailyWeatherList((prevDailyWeathers) => {
                const updated = [...prevDailyWeathers,dailyWeather];
                return updated;
            });
        }

    });*/

    const fetchData = ( async () => {
        const response1 = await fetch(`http://api.openweathermap.org/geo/1.0/direct?q=${params.cityId}&limit=5&appid=5a4e05662564bdefbc9d1b3d65c255f0`);
        const data1 = await response1.json();
        console.log("in fiveDayForecast.js ovo su geokoordinate");
        console.log(data1);


        //const response = await fetch(`https://api.openweathermap.org/data/2.5/onecall?q=${params.cityId}&units=metric&exclude=current,minutely,hourly,alerts&appid=5a4e05662564bdefbc9d1b3d65c255f0`);
        //const data = await response.json();
        //console.log(data);


        /*const dailyData = data.list.filter(reading => {   
            return reading.dt_txt.includes("18:00:00")
            }
          )
        console.log("ovo je filtrirano");
        console.log(dailyData);*/


        for (let i = 0; i <40; i=i+8) {

            let data_time = data.list[i].dt_txt.split(" ")[0].split("-");

            let month = getMonth(data_time[1]);
            var date = new Date(data_time[2]+month+data_time[0]);
            var day = date.getDay();
            var daysName = ['Sun','Mon','Tue','Wed','Thu','Fri','Sat'];


            const dailyWeather = {
                dayName : daysName[day],
                //icon : "data.list[i].weather[0].id",
                icon : <img src={`http://openweathermap.org/img/w/${data.list[i].weather[0].icon}.png`}/>,
                minTemp : data.list[i].main.temp_min,
                maxTemp : data.list[i].main.temp_max,

            }

            setDailyWeatherList((prevDailyWeathers) => {
                const updated = [...prevDailyWeathers,dailyWeather];
                return updated;
            });
        }

    });

    useEffect(() => {
        fetchData();
    }, []);    


    return(
        <div>
            <WeekContainer fiveDays={dailyWeatherList} />
        </div>
    );
}

export default FiveDayForecast;
