import React , {useState, useEffect} from 'react';
import Conditions from '../Conditions/Conditions';
import styles from './Forecast.module.css';
import ForecastList from './ForecastList';


const Forecast = () => {

    const [unit,setUnit] = useState("metric");
    const [enteredCity, setEnteredCity] = useState("");
    let [responseObj, setResponseObj] = useState({});

    let [error, setError] = useState(false);
    let [loading, setLoading] = useState(false);

    const [cityList, setCities] = useState([]);
    useEffect(() => {

    });

    //nacin1
    /*const getForecast =  async () => {
        try{
            const res = await axios.get(
                "https://api.openweathermap.org/data/2.5/weather?q=Zagreb&appid=5a4e05662564bdefbc9d1b3d65c255f0"
            )
            console.log(res.data);

        } catch(err){
            console.error(err);
        }
    };*/

    //nacin2
    /*const getForecast = async () => {
        console.log("halo");
        const res = await axios.get(
            "https://api.openweathermap.org/data/2.5/weather?q=Zagreb&appid=5a4e05662564bdefbc9d1b3d65c255f0"
        )
        console.log(res.data.main.temp);
        console.log(res.data.main.temp_max);
        console.log(res.data.main.temp_min);
    }*/


    const updateCityList = (responseCity) => {
        console.log("cityList ");
        console.log(cityList);
        console.log("response city ")
        console.log(responseCity)

        setCities((prevCities) => {
            return [...prevCities,responseCity];
          });

        console.log("cityList ");
        console.log(cityList);

    }

    function fetchForecastJSON() {
        const response = fetch(`https://api.openweathermap.org/data/2.5/weather?q=${enteredCity}&units=${unit}&appid=5a4e05662564bdefbc9d1b3d65c255f0`);
        // waits until the request completes...
        console.log("response ");
        console.log(response);
        const conditions = response.json();
        console.log("response ");
        console.log(response);
        return conditions;
    }
    //nacin3
    const getForecast = (e) => {
        e.preventDefault();
        console.log("daj prognozu!");

        if(enteredCity.trim().length === 0){
            console.log("ERROR MODAL ILI SAMO ZACRVENI");
            return setError(true);
        }
        //clear states for new data
        setError(false);
        setLoading(true);

        fetchForecastJSON()
        .then(response => {
            console.log("response = ");
            console.log(response);
            if(response.cod !== 200){
                setResponseObj(response)
                throw new Error()
            }
            setResponseObj(response)
            setLoading(false);
       })
       .catch(error => {
           setError(true);
           setLoading(false);

           console.log(error.message);
           console.log("uhvatio sam error!");
       })

       //console.log("evo citilist =");
       //console.log(cityList);
       const responseCity = {
           key : enteredCity,
           response : responseObj,
           error : error,
           loading : loading
       }
       updateCityList(responseCity);
       //console.log("evo citilist =");
       //console.log(cityList);

    }


    return(
        <div>
           <h2>Find Current Weather Conditions</h2>
           <form onSubmit={getForecast}>
                <input
                    type="text"
                    placeholder="Enter City"
                    maxLength="50"
                    value={enteredCity}
                    onChange={(e) => setEnteredCity(e.target.value)}
                    className={styles.TextInput}
                    />

                <label className={styles.Radio}>
                    <input
                        type="radio"
                        name="units"
                        checked={unit === "imperial"}
                        value="imperial"
                        onChange={(e) => setUnit(e.target.value)}
                        />
                    Fahrenheit
                </label>
                <label className={styles.Radio}>
                    <input
                        type="radio"
                        name="units"
                        checked={unit === "metric"}
                        value="metric"
                        onChange={(e) => setUnit(e.target.value)}
                        />
                    Celsius
                </label>
                <button className={styles.Button} type="submit">Get Forecast</button>
            </form>
                {<ForecastList cities={cityList}/>}
        </div>
    );

}

export default Forecast;
