import React , {useState} from 'react';
import Conditions from '../Conditions/Conditions';


const Forecast = () => {

    const [enteredCity, setEnteredCity] = useState("Beograd");
    let [responseObj, setResponseObj] = useState({});
    //nacin1
    /*const getForecast =  async () => {
        try{
            const res = await axios.get(
                "https://api.openweathermap.org/data/2.5/weather?q=Zagreb&appid=5a4e05662564bdefbc9d1b3d65c255f0"
            )
            console.log(res.data);

        } catch(err){
            console.error(err);
        }
    };*/

    //nacin2
    /*const getForecast = async () => {
        console.log("halo");
        const res = await axios.get(
            "https://api.openweathermap.org/data/2.5/weather?q=Zagreb&appid=5a4e05662564bdefbc9d1b3d65c255f0"
        )
        console.log(res.data.main.temp);
        console.log(res.data.main.temp_max);
        console.log(res.data.main.temp_min);
    }*/

    //nacin3
    const getForecast = () => {
        if(enteredCity.len === 0){
            console.log("ERROR MODAL ILI SAMO ZACRVENI");
        }
        fetch(`https://api.openweathermap.org/data/2.5/weather?q=${enteredCity}&appid=5a4e05662564bdefbc9d1b3d65c255f0`
        ).then(response => response.json()).then(response => {
           setResponseObj(response)
       })
    }
    const enteredCityHandler = (event) => {
        console.log("ovo je input: "+event.target.value);

        setEnteredCity(encodeURIComponent(event.target.value));
    };

    return(
        <div>
           <h2>Find Current Weather Conditions</h2>
           <input type="text" onChange={enteredCityHandler}></input>
           <button onClick={getForecast}>Get Forecast</button>
           <Conditions responseObj={responseObj}/>
       </div>
    );

}

export default Forecast;
