import react , {useEffect , useState} from "react";
import { useParams } from "react-router";

const FiveDayForecast = (props) => {

    const [data,setData] = useState();
    const [dailyWeather, setDailyWeather] = useState({});
    const params = useParams();


    const fetchData = ( async () => {
        const response = await fetch(`https://api.openweathermap.org/data/2.5/forecast?q=${params.cityId}&units=metric&appid=5a4e05662564bdefbc9d1b3d65c255f0`);
        const res = await response.json();
        console.log(res); 
        setData(res);
    });

    useEffect(() => {
        fetchData();
    }, []);

    let content = <p>Nema nista</p>;

    if (data) {

        content = <img src={`http://openweathermap.org/img/w/${data.list[0].weather[0].icon}.png`}/>
        console.log("datum je ");
        let d = data.list[0].dt_txt.split(" ")[0].split("-");
        console.log(d);
        var date = new Date(d[0],d[2],d[1]);
        var day = date.getDay();
        var daysName = ['Sunday','Monday','Tuesday','Wednesday','Thursday','Friday','Saturday'];
        console.log(daysName[day-1]);
        console.log("max temp je");
        console.log(data.list[0].main.temp_max);
        console.log("min temp je ");
        console.log(data.list[0].main.temp_min);
    }


    return(
        <div>
            <div>Ovo je petodnevna prognoza</div>
            <section>{content}</section>
        </div>
    );
}

export default FiveDayForecast;
