    const deleteCityHandler = (cityName) => {
        console.log("in forecast.js ovo je cityname= "+cityName);
        console.log(cityList);
        let updatedCities;
        updatedCities = cityList.filter(item => item.key !== cityName);
        setCities(updatedCities);


    }

    const ifExist = (enteredCity) => {
        console.log("provjeri postojanje grada "+enteredCity);
        for(var city in cityList){
            console.log("provjeravam grad ");
            console.log(cityList[city]);
            if (enteredCity === cityList[city].key){
                return true;
            }
        }
        return false;
    };

    const getForecast = useCallback(async (e) => {
        if(e){
            e.preventDefault();
        }
        console.log("entered city");
        console.log(enteredCity);


        //clear states for new data
        setError(false);
        setLoading(true);

        try{
            if (cityList.length === 10) {
                throw new Error("Max 10 cities allowed!");
            }

            if(enteredCity.trim().length === 0){
                throw new Error('Input is empty - try again!');
            }


            const response = await fetch(`https://api.openweathermap.org/data/2.5/weather?q=${enteredCity}&units=${unit}&appid=5a4e05662564bdefbc9d1b3d65c255f0`);
            if (!response.ok) {
                console.log("response bez json");
                console.log(response);
                throw new Error("Please enter valid city");
            }
            console.log("provjeravam je li ima dupli grad");
            if (ifExist(enteredCity.trim())){
                throw new Error('City is already listed!');
            }


            else {

                // waits until the request completes...
                const data = await response.json();

                setResponseObj(data);
                setLoading(false);

                const responseCity = {
                    key : enteredCity.trim(),
                    responseObj : data,
                    error : false,
                    loading : loading
                }

                //console.log("cityList ");
                //console.log(cityList);
                //console.log("response city ")
                //console.log(responseCity)

                setCities((prevCities) => {
                    const updated = [...prevCities,responseCity];
                    return updated;
                });
                //console.log("cityList ");
                //console.log(cityList);  
            }

        } catch (error) {
            setError(error.message);
            console.log("error message");
            //console.log(error);
        }
        setLoading(false);

    });

    let content = <p></p>;
    let errorContent = <p></p>;

    if (cityList.length > 0) {
        content = <ForecastList cities={cityList} onDelete={deleteCityHandler}/>;
        console.log(cityList);
    }

    if (error) {
        errorContent = <p className={styles.Small}>{error}</p>;
    }

    if (loading) {
        content = <p>Loading...</p>;
    }


    return(
        <div>
           <h2>Find Current Weather Conditions</h2>
           <form onSubmit={getForecast}>
                <input
                    type="text"
                    placeholder="Enter City"
                    maxLength="50"
                    value={enteredCity}
                    onChange={(e) => setEnteredCity(e.target.value)}
                    className={styles.TextInput}
                    />

                <label className={styles.Radio}>
                    <input
                        type="radio"
                        name="units"
                        checked={unit === "imperial"}
                        value="imperial"
                        onChange={(e) => setUnit(e.target.value)}
                        />
                    Fahrenheit
                </label>
                <label className={styles.Radio}>
                    <input
                        type="radio"
                        name="units"
                        checked={unit === "metric"}
                        value="metric"
                        onChange={(e) => setUnit(e.target.value)}
                        />
                    Celsius
                </label>
                <button className={styles.Button} type="submit">Get Forecast</button>
            </form>
            {error && errorContent}
            <section>{content}</section>
        </div>
    );

}
export default Forecast;
