import React , {useState, useEffect, useCallback} from 'react';
import styles from './Forecast.module.css';
import ForecastList from './ForecastList';


const Forecast = () => {

    const [unit,setUnit] = useState("metric");
    const [enteredCity, setEnteredCity] = useState("");
    let [responseObj, setResponseObj] = useState({});

    let [error, setError] = useState(false);
    let [loading, setLoading] = useState(false);
    const [cityList, setCities] = useState([]);


    /*const updateCityList = (responseCity) => {
        console.log("cityList ");
        console.log(cityList);
        console.log("response city ")
        console.log(responseCity)

        setCities((prevCities) => {
            return [...prevCities,responseCity];
          });

        console.log("cityList ");
        console.log(cityList);

    }*/

    async function fetchForecastJSON() {
        const response = await fetch(`https://api.openweathermap.org/data/2.5/weather?q=${enteredCity}&units=${unit}&appid=5a4e05662564bdefbc9d1b3d65c255f0`);
        // waits until the request completes...
        const conditions = await response.json();
        console.log("response bez json");
        console.log(response);
        return conditions;
    }
    //nacin3
    const getForecast = (e) => {
        e.preventDefault();
        console.log("daj prognozu!");

        if(enteredCity.trim().length === 0){
            console.log("ERROR MODAL ILI SAMO ZACRVENI");
            return setError(true);
        }
        //clear states for new data
        setError(false);
        setLoading(true);

        fetchForecastJSON()
        .then(response => {
            console.log("response sa json = ");
            console.log(response);
            if(response.cod !== 200){
                setResponseObj(response)
                throw new Error()
            }
            setResponseObj(response)
            setLoading(false);
            //console.log("evo citilist =");
            //console.log(cityList);
            const responseCity = {
                key : enteredCity,
                response : response,
                error : error,
                loading : loading
            }

            const curentCities = [];
            curentCities.push(responseCity);
            setCities(curentCities);

       })
       .catch(error => {
           setError(true);
           setLoading(false);

           console.log(error.message);
           console.log("uhvatio sam error!");
       })
    }


    return(
        <div>
           <h2>Find Current Weather Conditions</h2>
           <form onSubmit={getForecast}>
                <input
                    type="text"
                    placeholder="Enter City"
                    maxLength="50"
                    value={enteredCity}
                    onChange={(e) => setEnteredCity(e.target.value)}
                    className={styles.TextInput}
                    />

                <label className={styles.Radio}>
                    <input
                        type="radio"
                        name="units"
                        checked={unit === "imperial"}
                        value="imperial"
                        onChange={(e) => setUnit(e.target.value)}
                        />
                    Fahrenheit
                </label>
                <label className={styles.Radio}>
                    <input
                        type="radio"
                        name="units"
                        checked={unit === "metric"}
                        value="metric"
                        onChange={(e) => setUnit(e.target.value)}
                        />
                    Celsius
                </label>
                <button className={styles.Button} type="submit">Get Forecast</button>
            </form>
                {/*!loading && cityList.length > 0 && <ForecastList cities={cityList}/>*/}
                {!loading && cityList.length > 0 && <div>ISPIŠI</div>}
                {loading && <p>Loading...</p>}
        </div>
    );

}

export default Forecast;
